contracts/mock/EIP712Verifier.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "@openzeppelin/contracts/utils/cryptography/EIP712.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "../interfaces/ISignatureMintERC721.sol";

abstract contract EIP712Verifier is EIP712, ISignatureMintERC721 {
    function verify(
        MintRequest calldata req,
        address signer,
        bytes calldata signature
    ) external view {
        bytes32 digest = _hashTypedDataV4(
            keccak256(
                abi.encode(
                    keccak256("MintRequest(address to,string uri,uint256 tokenId)"),
                    req.to,
                    keccak256(bytes(req.uri)),
                    req.tokenId
                )
            )
        );
        address recoveredSigner = ECDSA.recover(digest, signature);
        require(recoveredSigner == signer);
    }
}

